#include <stdio.h>
#include <string.h>

#define MAX_TEXT_SIZE 8000
#define MAX_PATTERN_SIZE 100

void constructLps(const char *pat, int m, int lps[]) {
    int len = 0;
    lps[0] = 0;

    int i = 1;
    while (i < m) {
        if (pat[i] == pat[len]) {
            len++;
            lps[i] = len;
            i++;
        } else {
            if (len != 0) {
                len = lps[len - 1];
            } else {
                lps[i] = 0;
                i++;
            }
        }
    }
}

int search(const char *pat, const char *txt, int result_indices[], int max_results) {
    int m = strlen(pat);
    int n = strlen(txt);
    int lps[MAX_PATTERN_SIZE];
    int res_count = 0;

    constructLps(pat, m, lps);

    int i = 0;
    int j = 0;

    while (i < n) {
        if (txt[i] == pat[j]) {
            i++;
            j++;
            if (j == m) {
                if (res_count < max_results) {
                    result_indices[res_count++] = i - j;
                }
                j = lps[j - 1];
            }
        } else {
            if (j != 0) {
                j = lps[j - 1];
            } else {
                i++;
            }
        }
    }

    return res_count;
}

int main() {
    const char txt[] = "";
    const char pat[] = "ATGC";

    int result_indices[10]; //limitar as ocorrencias
    int count = search(pat, txt, result_indices, 10);

    for (int i = 0; i < count; i++) {
        printf("%d ", result_indices[i]);
    }

    printf("\n");
    return 0;
}
